export default function AIChatStreamingBlock() {
  const [messages, setMessages] = React.useState<Message[]>([
    {
      id: "1",
      role: "assistant",
      content:
        "Hello! I'm your AI assistant with streaming and thinking capabilities. How can I help you today?",
    },
